document.addEventListener("DOMContentLoaded",(()=>{let e=document.querySelectorAll(".input_range");for(let t of e)t.addEventListener("input",(()=>{t.nextElementSibling.style.width=t.value/t.max*100+"%",t.previousElementSibling.style.left=t.value/t.max*100+"%",t.parentElement.classList.contains("price")?t.previousElementSibling.innerHTML=`${t.value}$`:(t.parentElement.classList.contains("thc")||t.parentElement.classList.contains("cbd"))&&(t.previousElementSibling.innerHTML=`${t.value}%`)}));let t=document.querySelector(".tag-list"),n=document.querySelectorAll(".checkbox");t.addEventListener("click",(e=>{if(e.target.classList.contains("close")||e.target.parentElement.classList.contains("close")){e.target.closest("li").remove(),console.log(e.target.parentElement.previousElementSibling);for(let t of n)e.target.parentElement.previousElementSibling.innerHTML==t.nextElementSibling.innerHTML&&(t.checked=!1)}}));for(let e of n)e.addEventListener("change",(()=>{if(e.checked)t.innerHTML+=`\n          <li><span>${e.nextElementSibling.innerHTML}</span>\n          <button class="close"><img src="images/sprite.svg#close"></button>\n      </li >\n          `;else{let n=t.querySelectorAll("li");value=e.nextElementSibling.innerHTML;for(let e of n)e.firstElementChild.innerHTML.includes(value)&&e.remove()}}));let l=document.querySelectorAll(".checking");for(let e of l)e.addEventListener("click",(()=>{e.querySelector(".toggleBtn").classList.toggle("toggle")}));let i=document.querySelector("#menu");i.addEventListener("click",(()=>{i.nextElementSibling.classList.toggle("dropdown_active")}))}));